            <pie-chart-expenses :filter="filter"></pie-chart-expenses>
          </div>
        </div>
        <estimate-revenues v-if="query.theloai === 'dt-thu'" :filter="filter" :overview="overview"></estimate-revenues>
        <estimate-expenses v-if="query.theloai === 'dt-chi'" :filter="filter" :overview="overview"></estimate-expenses>
    </div>

</template>
<script>
    import EstimateRevenues from "./statebudgetestimates/EstimateRevenues";
    import EstimateExpenses from "./statebudgetestimates/EstimateExpenses";
    import PieChartRevenues from './statebudgetestimates/Estimate/PieChartRevenues';
    import PieChartExpenses from "./statebudgetestimates/Estimate/PieChartExpenses";
    import ApiService from '@/services/api.service';
    import Chart from 'chart.js';
    import 'chartjs-plugin-labels';
    import 'chartjs-plugin-zoom';

    export default {
        name: 'dashboard-module-state-budget-revenues',
        props: {
            filter: [Array, Object],
            query: [Array, Object]
        },
        data: function () {
            return {
              chartType: 1,
              isInitChart: true,
              overview: {
                dtt: null,
                dtt_nt: null,
                uth_t: null,
                dtc: null,
                dtc_nt: null,
                uth_c: null
              }
            }
        },
        components: {
          EstimateExpenses,
          EstimateRevenues,
          PieChartRevenues,
          PieChartExpenses
        },
        mounted() {
            if (this.isInitChart) {
                this.$nextTick(() => {
                    this.initChart();
                    this.isInitChart = false;
                });
            }
        },

        methods: {
          initChart(){
            this.fetchData();
          },
          fetchData(){
            let self = this;
            let requestData = {
              method: 'post',
              url: 'dashboard/api/state-budget-estimate',
              data: {
                filter: this.filter
              }
            };
            // Api edit user
            this.$store.commit('isLoading', true);
            ApiService.setHeader();
            ApiService.customRequest(requestData).then((responses) => {
              let responsesData = responses.data;
              if (responsesData.data) {
                self.overview.dtt = responsesData.data.dtt;
                self.overview.dtt_nt = responsesData.data.dtt_nt;
                self.overview.uth_t = responsesData.data.uth_t;
                self.overview.dtc = responsesData.data.dtc;
                self.overview.dtc_nt = responsesData.data.dtc_nt;
                self.overview.uth_c = responsesData.data.uth_c;
              }

              self.$store.commit('isLoading', false);
            }, (error) => {
              console.log(error);
              self.$store.commit('isLoading', false);
            });
          },
          changeChart(type) {
            this.query.theloai = type;
          },
          changeBreadcrumb(e, url){
            e.preventDefault();
            e.stopPropagation();
            this.$router.push({
                path: url,
            });
          },
          updateData(chart, labels = [], datasets, title = '') {
              if (labels.length) {
                  chart.data.labels = labels;
              }
              chart.data.datasets.forEach((item, key) => {
                  if (datasets && datasets[key] && datasets[key].data) {
                      item.data = datasets[key].data;
                  }
                  if (datasets && datasets[key] && datasets[key].backgroundColor) {
                      item.backgroundColor = datasets[key].backgroundColor;
                  }
                  if (datasets && datasets[key] && datasets[key].borderColor) {
                      item.borderColor = datasets[key].borderColor;
                  }
              });
              if (title) {
                  chart.options.title.title = title;
                  chart.config.options.title.text = title;
                  chart.config.options.title.title = title;
              }
              chart.update();
          },
        },
        watch: {
          '$route'(to, from){
            if (!this.isInitChart) {
              this.$nextTick(() => {
                  this.initChart();
              });
            }
          }
        }
    }
</script>

