      changeFilter() {
        let self = this;
        let ratePeriod = 1;
        if (this.filter.PeriodID === 6) {
          ratePeriod = _.random(0.3, 0.7);
        }else if (this.filter.PeriodID === 7) {
          ratePeriod = _.random(0.5, 0.9);
        }else if (this.filter.PeriodID === 8) {
          ratePeriod = _.random(2.5, 3.5);
        }else if (this.filter.PeriodID === 9) {
          ratePeriod = _.random(3.5, 8.5);
        }else if (this.filter.PeriodID === 10) {
          ratePeriod = _.random(7.5, 13.5);
        }

        let rateDistrict = 1;
        let rateCommune = 1;
        let rateRevenueCate = _.random(0.2, 0.8);

        if (this.filter.PeriodID === 1 && Number(this.filter.PeriodValue) > 2020){
          this.overviewNumber.totalEstimateRevenue = 0;
          this.overviewNumber.totalRevenue = 0;
          this.overviewNumber.totalSettlementRevenue = 0;
          this.overviewNumber.totalEstimateExpense = 0;
          this.overviewNumber.totalExpense = 0;
          this.overviewNumber.totalSettlementExpense = 0;
          return;
        }

        if (!this.overviewNumber.totalRevenue) {
          this.overviewNumber.totalRevenue = _.random(15000, 33650);
        }else if (this.filter.ProvinceID === 36 && !this.filter.DistrictID && !this.filter.CommuneID) {
          if (!this.filter.RevenueCateID) {
            return;
          }
        }

        if (this.filter.ProvinceID && !this.filter.DistrictID && !this.filter.CommuneID) {
          this.overviewNumber.totalEstimateRevenue = this.overviewNumber.totalEstimateRevenue * ratePeriod;
          this.overviewNumber.totalRevenue = this.overviewNumber.totalRevenue * ratePeriod;
          this.overviewNumber.totalSettlementRevenue = this.overviewNumber.totalSettlementRevenue * ratePeriod;
          this.overviewNumber.totalEstimateExpense = this.overviewNumber.totalEstimateExpense * ratePeriod;
          this.overviewNumber.totalExpense = this.overviewNumber.totalExpense * ratePeriod;
          this.overviewNumber.totalSettlementExpense = this.overviewNumber.totalSettlementExpense * ratePeriod;

        }else if (this.filter.ProvinceID && this.filter.DistrictID && !this.filter.CommuneID) {
          rateDistrict = _.random(1 / 10, 1 / 25);
          this.overviewNumber.totalEstimateRevenue = this.overviewNumber.totalEstimateRevenue * ratePeriod * rateDistrict;
          this.overviewNumber.totalRevenue = this.overviewNumber.totalRevenue * ratePeriod * rateDistrict;
          this.overviewNumber.totalSettlementRevenue = this.overviewNumber.totalSettlementRevenue * ratePeriod * rateDistrict;
          this.overviewNumber.totalEstimateExpense = this.overviewNumber.totalEstimateExpense * ratePeriod * rateDistrict;
          this.overviewNumber.totalExpense = this.overviewNumber.totalExpense * ratePeriod * rateDistrict;
          this.overviewNumber.totalSettlementExpense = this.overviewNumber.totalSettlementExpense * ratePeriod * rateDistrict;
        }else if (this.filter.CommuneID) {
          rateDistrict = _.random(1 / 10, 1 / 25);
          rateCommune = _.random(1 / 40, 1 / 70);
          this.overviewNumber.totalEstimateRevenue = this.overviewNumber.totalEstimateRevenue * ratePeriod * rateDistrict * rateCommune;
          this.overviewNumber.totalRevenue = this.overviewNumber.totalRevenue * ratePeriod * rateDistrict * rateCommune;
          this.overviewNumber.totalSettlementRevenue = this.overviewNumber.totalSettlementRevenue * ratePeriod * rateDistrict * rateCommune;
          this.overviewNumber.totalEstimateExpense = this.overviewNumber.totalEstimateExpense * ratePeriod * rateDistrict * rateCommune;
          this.overviewNumber.totalExpense = this.overviewNumber.totalExpense * ratePeriod * rateDistrict * rateCommune;
          this.overviewNumber.totalSettlementExpense = this.overviewNumber.totalSettlementExpense * ratePeriod * rateDistrict * rateCommune;
        }
        this.overviewNumber.totalEstimateRevenue = Math.floor(this.overviewNumber.totalEstimateRevenue);
        this.overviewNumber.totalRevenue = Math.floor(this.overviewNumber.totalRevenue);
        this.overviewNumber.totalSettlementRevenue = Math.floor(this.overviewNumber.totalSettlementRevenue);
        this.overviewNumber.totalEstimateExpense = Math.floor(this.overviewNumber.totalEstimateExpense);
        this.overviewNumber.totalExpense = Math.floor(this.overviewNumber.totalExpense);
        this.overviewNumber.totalSettlementExpense = Math.floor(this.overviewNumber.totalSettlementExpense);
      },
      resetOverviewNumber() {
        if (this.filter.ProvinceID === 36) {
          if (!this.filter.DistrictID && !this.filter.CommuneID) {
            let ratePeriod = 1;
            if (this.filter.PeriodID === 6) {
              ratePeriod = _.random(0.3, 0.7);
            }else if (this.filter.PeriodID === 7) {
              ratePeriod = _.random(0.5, 0.9);
            }else if (this.filter.PeriodID === 8) {
              ratePeriod = _.random(2.5, 3.5);
            }else if (this.filter.PeriodID === 9) {
              ratePeriod = _.random(3.5, 8.5);
            }else if (this.filter.PeriodID === 10) {
              ratePeriod = _.random(7.5, 13.5);
            }

            this.overviewNumber.totalEstimateRevenue = 22409 * ratePeriod;
            this.overviewNumber.totalEstimateRevenue = Math.floor(this.overviewNumber.totalEstimateRevenue);

            this.overviewNumber.totalRevenue = 16622 * ratePeriod;
            this.overviewNumber.totalRevenue = Math.floor(this.overviewNumber.totalRevenue);

            this.overviewNumber.totalSettlementRevenue = 16622 * ratePeriod;
            this.overviewNumber.totalSettlementRevenue = Math.floor(this.overviewNumber.totalSettlementRevenue);

            this.overviewNumber.totalEstimateExpense = 18488 * ratePeriod;
            this.overviewNumber.totalEstimateExpense = Math.floor(this.overviewNumber.totalEstimateExpense);

            this.overviewNumber.totalExpense = 19455 * ratePeriod;
            this.overviewNumber.totalExpense = Math.floor(this.overviewNumber.totalExpense);

            this.overviewNumber.totalSettlementExpense = 18111 * ratePeriod;
            this.overviewNumber.totalSettlementExpense = Math.floor(this.overviewNumber.totalSettlementExpense);

          }else {
            this.overviewNumber.totalEstimateRevenue = 22409;
            this.overviewNumber.totalRevenue = 16622;
            this.overviewNumber.totalSettlementRevenue = 16622;
            this.overviewNumber.totalEstimateExpense = 18488;
            this.overviewNumber.totalExpense = 19455;
            this.overviewNumber.totalSettlementExpense = 18111;
          }
        }else {
          this.overviewNumber.totalEstimateRevenue = 0;
          this.overviewNumber.totalRevenue = 0;
          this.overviewNumber.totalSettlementRevenue = 0;
          this.overviewNumber.totalEstimateExpense = 0;
          this.overviewNumber.totalExpense = 0;
          this.overviewNumber.totalSettlementExpense = 0;
        }

        if (this.filter.PeriodID === 1 && this.filter.PeriodValue != 2020) {
          this.overviewNumber.totalEstimateRevenue = 0;
          this.overviewNumber.totalRevenue = 0;
          this.overviewNumber.totalSettlementRevenue = 0;
          this.overviewNumber.totalEstimateExpense = 0;
          this.overviewNumber.totalExpense = 0;
          this.overviewNumber.totalSettlementExpense = 0;
        }else if (this.filter.PeriodID === 1 && this.filter.PeriodValue == 2020){
          this.overviewNumber.totalEstimateRevenue = 22409;
          this.overviewNumber.totalRevenue = 16622;
          this.overviewNumber.totalSettlementRevenue = 16622;
          this.overviewNumber.totalEstimateExpense = 18488;
          this.overviewNumber.totalExpense = 19455;
          this.overviewNumber.totalSettlementExpense = 18111;
        }
      }
    },
